package Interview_Java_Naveen_86;

public class SwapNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		System.out.println("Before Swap----------");
		System.out.println("a="+a);
		System.out.println("b="+b);
		
		System.out.println("Before Swap----------");
		int temp;
		temp=a;
		a=b;
		b=temp;
		
		System.out.println("a="+a);
		System.out.println("b="+b);
		
	}

}
