package Java_MarchSession;

public class Vid2_Ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=20;
		if(num==10){
			System.out.println("num is 10");
		}
		else if(num==29){
			System.out.println("num is 29");
		}
		else if(num==20){
			System.out.println("num is 20");
		}
	}

}
