package Java_MarchSession;

public class Vid3_IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int j=i++; //post increment
		//post increment
		System.out.println("Post increment: Means ist given then increment");
		System.out.println(i);
		System.out.println(j);
		
		System.out.println("Pre increment: Means ist increment then give");
		int p=1;
		int q=++p; //pre increment
		//post increment
		
		System.out.println(p);
		System.out.println(q);
		
		System.out.println("Pre increment: Means ist decrement then give");
		int g=2;
		int h=--g; //pre decrement
		//post increment
		
		System.out.println(g);
		System.out.println(h);



		System.out.println("Pre increment: Means ist decrement then give");
		int c=-2;
		int d=--c; //pre decrement
		//post increment
		
		System.out.println(c);
		System.out.println(d);

	}

}
