package Youtube_Naveen_Basic_Core_Java;

public class Vid1_DataType {

	//main () method ->starting execution point of program
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primitive datatype->int,double,char,boolean
		//String is not a primitive datatype and its  a class
		
		//1.int
		int i=10;
		i=20;
		System.out.println(i);
		//2.double
		double d=12.44;
		double d1=100;//100.00
		System.out.println(d);
		System.out.println(d1);
		//3.char //single digit with in single quote
		char c='A';
		System.out.println(c);
		//4.boolean
		boolean b1=true;
		boolean b2=false;
		System.out.println(b1);
		//5.String ->its a class and not a datatype
		String s1="hellp world";
		String s2="Selenium";
		System.out.println(s2);
		
		

	}

}
